    <script>

    var ConfirmDelete = function (Id) {
        $(""#hiddenId"").val(Id);
        $(""#deleteModal"").modal('show');
    }
    var XoaSanPham = function () {
        $(""#loaderDiv1"").show();
        var Id = $(""#hiddenId"").val();

        $.ajax({
            type: ""GET"",
            url: ""/BaiViet/DeleteArticle"",
            data: { id: Id },
            success: function (result) {
                $(""#loaderDiv1"").hide();
                $(""#deleteModal"").modal(""hide"");
                $(""#row_"" + Id).remove();
                Notiflix.Notify.Success('Xóa thành công');
            }
        })
    }

        var AddEditBaiViet = function (id) {
            var url = ""/BaiViet/AddEditArticle?Id="" + id;
        $(""#myModalBodyDiv1"").load(url, function () {
            $(""#AddBaiVietModal"").modal(""show"")
        })
    }
    </script>
